import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState([]);
  const [selectedPokemonId, setSelectedPokemonId] = useState([]);

  useEffect(() => {
    getPokemons();
  }, []);

  const [selectedPokemon, setSelectedPokemon] = useState([]);
  
  const handleCardClick = (event, pokemonId) => {
    event.stopPropagation();
    setSelectedPokemonId((prevId) => (prevId === pokemonId ? null : pokemonId));
  };

  const getPokemons = async () => {
    const res = await axios.get(API_URI);
    setPokemon(res.data);
  };
  const searcher = (e) => {
    setSearch(e.target.value);
  };
  let filteredPokemons = pokemons;
  if (search) {
    filteredPokemons = pokemons.filter((data) =>
      String(data.name).toLowerCase().includes(String(search).toLowerCase())
    );
  }
  return (
    <div className="container">
      <div className="row pt-5 pb-4">
        <div className="col-12">
          <input
            value={search}
            onChange={searcher}
            type="text"
            placeholder="Filtra pokemons por nombre..."
            className="form-control filter text-center rounded-0"
          />
        </div>
      </div>
      <div className="row ">
        {filteredPokemons.map((pokemon) => (
          <div
            key={pokemon.id}
            className={`super-card col-md-4 mb-4  justify-content-center align-items-center d-flex align-items-stretch  ${
              selectedPokemon && selectedPokemon.id === pokemon.id
                ? "selected"
                : ""
            }`}
            onClick={(e) => handleCardClick(e, pokemon)}
          >
            <div className="card">
              <div className="card-header position-relative px-0 pb-0 mb-0 d-flex align-items-center">
                <img
                  className="card-img-top mx-auto"
                  src={pokemon.img}
                  alt="Card image cap"
                />
                <p className="id-card-text position-absolute bottom-0 left-0 mb-0 px-3 pt-1 pb-1 fw-bold">
                  ID / {pokemon.id}
                </p>
              </div>

              <div className="card-body">
                <div className="row">
                  <h4 className="card-title text-start text-capitalize mb-0">
                    {pokemon.name}
                  </h4>
                </div>
                <div className="row">
                  <p className="text-start">
                    {pokemon.Types.map((types) => (
                      <span className="badge pokemon me-1 d-inline-block  text-uppercase fw-light">
                        {types.name}
                      </span>
                    ))}
                  </p>
                </div>
                {pokemon.evolvesFrom !== "n/a" ? (
                  <div className="row">
                    <div className=" position-relative">
                      <div className="evolution-text text-start">
                        <p className="fw-light mb-0 ms-2">Evoluciona de:</p>
                        <h4 className="fw-light ms-2">{pokemon.evolvesFrom}</h4>
                      </div>
                    </div>
                  </div>
                ) : (
                  ""
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CompShowPokemons;
