import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonCard from "./PokemonCard";
import PokemonSearcher from "./PokemonSearcher";
import { useSpring, animated } from 'react-spring';

const API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState("");
  const [toggledPokemons, setToggledPokemons] = useState({});

  useEffect(() => {
    getPokemons();
  }, []);

  const getPokemons = async () => {
    try {
      const res = await axios.get(API_URI);
      setPokemon(res.data);
    } catch (error) {
      console.error("Error fetching pokemons:", error);
    }
  };

  const filterPokemons = () => {
    if (search) {
      return pokemons.filter((data) =>
        String(data.name).toLowerCase().includes(String(search).toLowerCase())
      );
    }
    return pokemons;
  };

  const togglePokemon = (id) => {
    setToggledPokemons((prevToggledPokemons) => ({
      prevToggledPokemons,
      [id]: !prevToggledPokemons[id] || false,
    }));
  };

  const calculatePokemonStyle = (pokemon) => ({
    width: toggledPokemons[pokemon.id] ? '100%' : '33%',
    position: toggledPokemons[pokemon.id] ? 'absolute' : 'relative',
    zIndex: toggledPokemons[pokemon.id] ? '111' : '0',
    height: toggledPokemons[pokemon.id] ? '100v' : '0',
    
  });

return (
  <>
    <div className="container">
      <div className="row">
        <div className="col-12">
          <PokemonSearcher value={search} onChange={(e) => setSearch(e.target.value)} />
        </div>
      </div>
    </div>
    <div className="container">
      <div className="row position-relative">
        {filterPokemons().map((pokemon) => (
          <animated.div
            key={pokemon.id}
            style={calculatePokemonStyle(pokemon)}
            onClick={() => togglePokemon(pokemon.id)}
            className="col-md-4 mb-4 justify-content-center align-items-center d-flex align-items-stretch"
          >
            <PokemonCard pokemon={pokemon} />
          </animated.div>
        ))}
      </div>
    </div>
  </>
);
};

export default CompShowPokemons;
