import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonSearcher from "./PokemonSearcher";
import AnimatedPokemonCard from "./AnimatedPokemonCard";
import { useParams } from "react-router-dom";
let API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState("");
   


  
  useEffect(() => {
    getPokemons();
  }, []);
  
  const getPokemons = async () => {
   
    try {
      const res = await axios.get(API_URI);
      setPokemon(res.data);
    } catch (error) {
      console.error("Error fetching pokemons:", error);
    }
  };

  const filterPokemons = () => {
    if (search) {
      return pokemons.filter((data) =>
        String(data.name).toLowerCase().includes(String(search).toLowerCase())
      );
    }
    return pokemons;
  };

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-12">
            <PokemonSearcher value={search} onChange={(e) => setSearch(e.target.value)} />
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row position-relative">
           

            filterPokemons().slice(0, 6).map((pokemon) => (
              <AnimatedPokemonCard key={pokemon.id} pokemon={pokemon} />
            ))
          ) : (
            // Entra aquí si id.id existe
            // Puedes agregar lógica adicional si es necesario

            <AnimatedPokemonCard key={pokemons.id} pokemon={pokemons} />
            

          )} 
        </div>
      </div>
    </>
  );
};

export default CompShowPokemons;
