import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonCard from "./PokemonCard";
import PokemonSearcher from "./PokemonSearcher";
import { useSpring, animated } from 'react-spring';

const API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState("");
  const [toggledStates, setToggledStates] = useState([]);

  useEffect(() => {
    getPokemons();
  }, []);

  const getPokemons = async () => {
    try {
      const res = await axios.get(API_URI);
      // Inicializar los estados locales para cada Pokémon
      setToggledStates(res.data.map(() => false));
      setPokemon(res.data);
    } catch (error) {
      console.error("Error fetching pokemons:", error);
    }
  };

  const handleToggle = (index) => {
    // Actualizar el estado local del Pokémon en la posición index
    const newToggledStates = [...toggledStates];
    newToggledStates[index] = !newToggledStates[index];
    setToggledStates(newToggledStates);
  };

  const filterPokemons = () => {
    if (search) {
      return pokemons.filter((data) =>
        String(data.name).toLowerCase().includes(String(search).toLowerCase())
      );
    }
    return pokemons;
  };

  const fade = useSpring({
    width: toggledStates.map((toggled) => toggled ? '100%' : '33%'),
  });

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-12">
            <PokemonSearcher value={search} onChange={(e) => setSearch(e.target.value)} />
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row position-relative">
          {filterPokemons().map((pokemon, index) => (
            <animated.div
              key={pokemon.id}
              style={fade}
              onClick={() => handleToggle(index)}
              className="col-md-4 mb-4 justify-content-center align-items-center d-flex align-items-stretch"
            >
              <PokemonCard pokemon={pokemon} />
            </animated.div>
          ))}
        </div>
      </div>
    </>
  );
};

export default CompShowPokemons;
