import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import PokemonCard from "./PokemonCard";
import PokemonSearcher from "./PokemonSearcher";

const API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  // Estado
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedPokemon, setSelectedPokemon] = useState(null);

  // Efecto para cargar los pokemons al montar el componente
  useEffect(() => {
    getPokemons();
  }, []);

  // Función para manejar el clic en una tarjeta de pokemon
  const handleCardClick = (pokemon) => {
    setSelectedPokemon(pokemon);
  };

  // Función para obtener los pokemons desde la API
  const getPokemons = async () => {
    try {
      const res = await axios.get(API_URI);
      setPokemon(res.data);
    } catch (error) {
      console.error("Error fetching pokemons:", error);
    }
  };

  // Función para filtrar los pokemons según la búsqueda
  const filterPokemons = () => {
    if (search) {
      return pokemons.filter((data) =>
        String(data.name).toLowerCase().includes(String(search).toLowerCase())
      );
    }
    return pokemons;
  };

  // Lógica principal
  const filteredPokemons = filterPokemons();
  return (
    <div className="container">
      <div className="row pt-5 pb-4">
        <PokemonSearcher value={search} onChange={(e) => setSearch(e.target.value)} />
      </div>
      <div className="row ">
        {filteredPokemons.map((pokemon) => (
          <div className="col-md-4 mb-4  justify-content-center align-items-center d-flex align-items-stretch ">
            <PokemonCard pokemon={pokemon} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default CompShowPokemons;
