import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  useEffect(() => {
    getPokemons();
  }, []);
  const getPokemons = async () => {
    const res = await axios.get(API_URI);
    setPokemon(res.data);
  };

  return (
    <div className="container">
      <div className="row pt-5">
        {pokemons.map((pokemon) => (
          <div className="col-md-4 mb-4  justify-content-center align-items-center">
            <div className="card" key="{pokemon.id}">
              <div className="card-header position-relative px-0 pb-0 mb-0">
                <img
                  className="card-img-top"
                  src={pokemon.img}
                  alt="Card image cap"
                />
                <p className="id-card-text position-absolute bottom-0 left-0 mb-0 px-3 pt-1 pb-1 fw-bold">
                  ID / {pokemon.id}
                </p>
              </div>

              <div className="card-body">
                <div className="row">
                  <h4 className="card-title text-start text-capitalize">
                    {pokemon.name}
                  </h4>
                </div>
                <div className="row">
                  <p className="text-start">
                  {pokemon.Types.map((types) => (
                     <span className="badge pokemon me-1   text-uppercase fw-light">{types.name}</span>
                  ))}
                   
                  </p>
                </div>
                <div className="row">
                    <div className="evolution-text">
                        <p></p>
                    </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CompShowPokemons;
