import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonSearcher from "./PokemonSearcher";
import AnimatedPokemonCard from "./AnimatedPokemonCard";
import { useParams } from "react-router-dom";


const ShowSinglePokemon = () => {
    const [pokemon, setPokemon] = useState([]);
    const id = useParams();
    const API_URI = "http://localhost:8080/pokemons/" + id.id + "/";


    useEffect(() => {
        getPokemon();
    }, []);

    const getPokemon = async () => {

        try {
            const res = await axios.get(API_URI);
            setPokemon(res.data);
        } catch (error) {
            console.error("Error fetching pokemon:", error);
        }
    };


    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-12">
                        <Link to="/" className="btn btn-danger">Volver</Link>
                    </div>
                </div>
            </div>
            <div className="container">
                <div className="row position-relative">
                    <AnimatedPokemonCard key={pokemon.id} pokemon={pokemon} />
                </div>
            </div>
        </>
    );
};

export default ShowSinglePokemon;
