import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import PokemonCard from "./PokemonCard";

const API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState([]);

  useEffect(() => {
    getPokemons();
  }, []);

  const [selectedPokemon, setSelectedPokemon] = useState([]);


  const handleCardClick = (pokemon) => {
    setSelectedPokemon(pokemon);
  };


  const getPokemons = async () => {
    const res = await axios.get(API_URI);
    setPokemon(res.data);
  };
  const searcher = (e) => {
    setSearch(e.target.value);
  };
  let filteredPokemons = pokemons;
  if (search) {
    filteredPokemons = pokemons.filter((data) =>
      String(data.name).toLowerCase().includes(String(search).toLowerCase())
    );
  }
  return (
    <div className="container">
      <div className="row pt-5 pb-4">
      <Pok value={search} onChange={(e) => setSearch(e.target.value)} />
      </div>
      <div className="row ">
        {filteredPokemons.map((pokemon) => (
          <div className="col-md-4 mb-4  justify-content-center align-items-center d-flex align-items-stretch ">
            <PokemonCard pokemon={pokemon} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default CompShowPokemons;
