import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonSearcher from "./PokemonSearcher";
import AnimatedPokemonCard from "./AnimatedPokemonCard";
const itemsPerPage = 6; // Define la cantidad de elementos por página

let API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState("");
  const [pages, setPages] = useState([]);
  const [filteredPokemons, setFilteredPokemons] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    getPokemons();
  }, []);
  useEffect(() => {
    filterPokemons();
  }, [search, pokemons]);
  const getPokemons = async () => {
    try {
      const res = await axios.get(API_URI);
      setPokemon(res.data);
    } catch (error) {
      console.error("Error fetching pokemons:", error);
    }
  };

  // Método para filtrar pokemons en función de la búsqueda
  const filterPokemons = () => {
    if (search) {
      const filtered = pokemons.filter((data) =>
        String(data.name).toLowerCase().includes(String(search).toLowerCase())
      );
      setFilteredPokemons(filtered);
    } else {
      setFilteredPokemons(pokemons);
    }
  };


  // Actualizar las páginas cuando cambia la lista de pokemons
  useEffect(() => {
    let workingPages = [];
    let workingPagesCurrentIndex = 0;

    if (filteredPokemons.length) {
      filteredPokemons.forEach((pokemon) => {
        if (
          workingPages[workingPagesCurrentIndex] &&
          workingPages[workingPagesCurrentIndex].length === itemsPerPage
        )
          workingPagesCurrentIndex++;

        if (workingPages[workingPagesCurrentIndex] === undefined)
          workingPages[workingPagesCurrentIndex] = [];

        workingPages[workingPagesCurrentIndex].push(pokemon);
      });
    }

    setPages([...workingPages]);
  }, [filteredPokemons, currentPage]);

  // Resto del código...
  const handlePageChange = (pageNo) => {
    setCurrentPage(pageNo);
  };
  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-12">
            <PokemonSearcher
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row position-relative">
          {pages.length &&
            pages[currentPage] &&
            pages[currentPage].map((pokemon) => (
              <AnimatedPokemonCard key={pokemon.id} pokemon={pokemon} />
            ))}
        </div>
        <div>
          <button
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 0}
          >
            Previous
          </button>
          <button
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage === pages.length - 1}
          >
            Next
          </button>
        </div>
      </div>
    </>
  );
};

export default CompShowPokemons;
