import React, { useState, useRef } from "react";
import PokemonCard from "./PokemonCard";
import { useSpring, animated } from 'react-spring';

const AnimatedPokemonCard = ({ pokemon }) => {
    const [isToggled, setToggle] = useState(false);
    const elementRef = useRef(null);
    const props = useSpring({
        from: {
          zIndex: isToggled ? 111 : 1,
          position: isToggled ? 'absolute' : 'relative',
          width: isToggled ? '100%' : '33%',
          height: isToggled ? elementRef.current.parentElement.offsetHeight : 'auto',
        },
        to: async (next, cancel) => {
          await next({ zIndex: isToggled ? 1 : 111 });
          await next({ position: isToggled ? 'relative' : 'absolute' });
          await next({ width: isToggled ? '33%' : '100%' });
          await next({ height: isToggled ? 'auto' : elementRef.current.parentElement.offsetHeight });
        },
      });
    console.log(props);
    return (<animated.div
        ref={elementRef}
        id={pokemon.id}
        key={pokemon.id}
        style={props}
        onClick={() => setToggle(!isToggled)}
        className="col-md-4 mb-4 justify-content-center align-items-center d-flex align-items-stretch"
    >
        <PokemonCard pokemon={pokemon} />
    </animated.div>);
};
export default AnimatedPokemonCard;
