// ...

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState("");
  const [toggledPokemons, setToggledPokemons] = useState({});

  useEffect(() => {
    getPokemons();
  }, []);

  const getPokemons = async () => {
    try {
      const res = await axios.get(API_URI);
      setPokemon(res.data);
    } catch (error) {
      console.error("Error fetching pokemons:", error);
    }
  };

  const filterPokemons = () => {
    if (search) {
      return pokemons.filter((data) =>
        String(data.name).toLowerCase().includes(String(search).toLowerCase())
      );
    }
    return pokemons;
  };

  // Mover useSpring al nivel superior del componente
  const props = useSpring({
    width: toggledPokemons[id] ? '100%' : '33%',  // Cambiar a id en lugar de pokemon.id
    position: toggledPokemons[id] ? 'absolute' : 'relative',
    zIndex: toggledPokemons[id] ? '111' : '0',
    height: toggledPokemons[id] ? '100vh' : 'auto',
  });

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-12">
            <PokemonSearcher value={search} onChange={(e) => setSearch(e.target.value)} />
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row position-relative">
          {filterPokemons().map((pokemon) => (
            <animated.div
              key={pokemon.id}
              style={props}  // Usar el objeto props aquí
              onClick={() => setToggledPokemons((prev) => ({ ...prev, [pokemon.id]: !prev[pokemon.id] || false }))}
              className="col-md-4 mb-4 justify-content-center align-items-center d-flex align-items-stretch"
            >
              <PokemonCard pokemon={pokemon} />
            </animated.div>
          ))}
        </div>
      </div>
    </>
  );
};

