import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonCard from "./PokemonCard";
import PokemonSearcher from "./PokemonSearcher";

const API_URI = "http://localhost:8080/pokemons/";
const ITEMS_PER_PAGE = 6; // Número de elementos por página

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedPokemon, setSelectedPokemon] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    getPokemons();
  }, [search, currentPage]);

  const handleCardClick = (pokemon) => {
    setSelectedPokemon(pokemon);
  };

  const getPokemons = async () => {
    try {
      const res = await axios.get(API_URI);
      setPokemon(res.data);
    } catch (error) {
      console.error("Error fetching pokemons:", error);
    }
  };

  const filterPokemons = () => {
    if (search) {
      return pokemons.filter((data) =>
        String(data.name).toLowerCase().includes(String(search).toLowerCase())
      );
    }
    return pokemons;
  };

  const paginatedPokemons = () => {
    const filtered = filterPokemons();
    const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
    const endIndex = startIndex + ITEMS_PER_PAGE;
    return filtered.slice(startIndex, endIndex);
  };

  const totalPages = Math.ceil(filterPokemons().length / ITEMS_PER_PAGE);

  const changePage = (page) => {
    setCurrentPage(page);
  };

  const hasPrevPage = currentPage > 1;
  const hasNextPage = currentPage < totalPages;

  const handleSearchChange = (newValue) => {
    setSearch(newValue);
    setCurrentPage(1); // Resetear la página cuando cambia el término de búsqueda
  };



  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-12">
            <PokemonSearcher value={search} onChange={(e) => setSearch(e.target.value)} />
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row">
          {paginatedPokemons().map((pokemon) => (
            <div key={pokemon.id} className="col-md-4 mb-4 justify-content-center align-items-center d-flex align-items-stretch">
              <PokemonCard pokemon={pokemon} onClick={handleCardClick} />
            </div>
          ))}
        </div>
      </div>
      <div className="container mt-5">
        <div className="row">
          <div className="col-12 text-center">
            {hasPrevPage && (
              <button onClick={() => changePage(currentPage - 1)}>
                Anterior
              </button>
            )}
          
            {hasNextPage && (
              <button onClick={() => changePage(currentPage + 1)}>
                Siguiente
              </button>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default CompShowPokemons;
