import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonSearcher from "./PokemonSearcher";
import AnimatedPokemonCard from "./AnimatedPokemonCard";
import { useParams } from "react-router-dom";
let API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState("");
  const id = useParams().id;

  if (id && id.trim() !== "") {
    // El parámetro id no está vacío
    console.log("El parámetro id no está vacío:", id);
    // Realiza las operaciones que necesites con el id no vacío
  } else {
    // El parámetro id está vacío o es null/undefined
    console.log("El parámetro id está vacío o no existe.");
    // Realiza las operaciones que necesites en este caso
  }

  console.log(!id.id);
  useEffect(() => {
    getPokemons();
  }, []);

  const getPokemons = async () => {
    API_URI = (!id.id) ? (API_URI) : (API_URI + '' + id.id + '/');
    try {
      const res = await axios.get(API_URI);
      setPokemon(res.data);
    } catch (error) {
      console.error("Error fetching pokemons:", error);
    }
  };

  const filterPokemons = () => {
    if (search) {
      return pokemons.filter((data) =>
        String(data.name).toLowerCase().includes(String(search).toLowerCase())
      );
    }
    return pokemons;
  };

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-12">
            <PokemonSearcher value={search} onChange={(e) => setSearch(e.target.value)} />
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row position-relative">
        filterPokemons().slice(0, 6).map((pokemon) => (
              <AnimatedPokemonCard key={pokemon.id} pokemon={pokemon} />
            ))
        </div>
      </div>
    </>
  );
};

export default CompShowPokemons;
