import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const API_URI = "http://localhost:8080/pokemons/";

const CompShowPokemons = () => {
  const [pokemons, setPokemon] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    getPokemons();
  }, []);

  const [selectedPokemon, setSelectedPokemon] = useState(null);

  const handleCardClick = (pokemon) => {
    setSelectedPokemon(pokemon);
  };

  const getPokemons = async () => {
    const res = await axios.get(API_URI);
    setPokemon(res.data);
  };
  const searcher = (e) => {
    setSearch(e.target.value);
  };
  let filteredPokemons = pokemons;
  if (search) {
    filteredPokemons = pokemons.filter((data) =>
      String(data.name).toLowerCase().includes(String(search).toLowerCase())
    );
  }
  return (
    <div className="container">
      <div className="row pt-5 pb-4">
        <div className="col-12">
          <input
            value={search}
            onChange={searcher}
            type="text"
            placeholder="Filtra pokemons por nombre..."
            className="form-control filter text-center rounded-0"
          />
        </div>
      </div>
      <div className="row">
        {filteredPokemons.map((pokemon) => (
          <div
            key={pokemon.id}
            className={`super-card col-md-4 mb-4  justify-content-center align-items-center d-flex align-items-stretch  ${
              selectedPokemon && selectedPokemon.id === pokemon.id ? "selected" : ""
            }`}
            onClick={() => handleCardClick(pokemon)}
          >
            {/* Resto del c√≥digo */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CompShowPokemons;
